import turtle

def main():
    # Create a turtle object
    georgefloyd = turtle.Turtle()
    georgefloyd.color("BLue")
    georgefloyd.speed()


    # Ask the turtle to move around the canvas
    for i in range(99999999999999999999999):
        georgefloyd.forward(50 + (i))
        georgefloyd.right(300)


    turtle.exitonclick()

if __name__ == "__main__":
    main()


